import React from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import moment from 'moment';
import Axios from "axios";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Spinner from "react-bootstrap/Spinner";

class GamePage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bid: {},
            bidamount: undefined,
            isLoaded: false,
            credits: null
        }
    }
    componentDidMount() {
      this.getUser();
        Axios.get("http://localhost:3001/bid")
        .then((result) => {
          this.setState({
            isLoaded: true,
            bid: result.data
          });
        },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          });
    }

  getUser() {
    Axios.get('http://localhost:3001/user').then(response => {
      console.log(response.data);
      if (response.data.user) {
        console.log('Get User: There is a user saved in the server session: ');

        this.setState({
          loggedIn: true,
          username: response.data.user.username,
          credits: response.data.user.credits

        })
      } else {
        console.log('Get user: no user');
        this.setState({
          loggedIn: false,
          username: null
        });
      };
    });
  };
    handleSubmit(event) {
      event.preventDefault()
      console.log('handleSubmit')
      Axios
          .put('http://localhost:3001/user/', {
              username: this.state.username,
              password: this.state.password
          })
          .then(response => {
              console.log('login response: ')
              console.log(response)
              if (response.status === 200) {
                  // update App.js state
                  this.props.updateUser({
                      loggedIn: true,
                      username: response.data.username
                  })
                  // update the state to redirect to home
                  this.setState({
                      redirectTo: '/'
                  })
              }
          }).catch(error => {
              console.log('login error: ')
              console.log(error);
          });
  };
    render() {

        const {bid, isLoaded, credits} = this.state;
       console.log(bid);
       chosenGame = {};
        for(var i = 0; i < bid.length; i++) {

        }
        if (!isLoaded) {
            return <Spinner animation="grow" variant="warning" />
          } else {
            return (

            <Col md={{ span: 6, offset: 3 }}>
            <Card className="jawn" bg="dark" text="white">
                <Card.Text>Credits: {this.state.credits}</Card.Text>
                <Card.Text>Betting Ticket</Card.Text>
                <Card.Body>
      <Card.Title>{bid.teamOneName} vs {bid.teamTwoName}</Card.Title>

      <Card.Text>
        Time: {moment(new Date(parseInt(bid.commencementTime * 1000))).format('MMMM Do YYYY, h:mm a')}
      </Card.Text>
      <Form.Label>Bid Amount</Form.Label>
								<Form.Control
									type="bidamount"
									placeholder="$0.00"
									id="bidamount"
									name="bidamount"
									value={this.state.username}
									onChange={this.handleChange} />
    	<Button onClick={this.handleSubmit} variant="warning" type="submit">
							Submit
  						</Button>

    </Card.Body>
            </Card>
            </Col>
        );
    };
};
};
export default GamePage;
