import React, { Component } from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import moment from 'moment';
import Axios from "axios";
import Col from "react-bootstrap/Col";
import ButtonToolbar from "react-bootstrap/ButtonToolbar";
import Spinner from "react-bootstrap/Spinner";
import { MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from "mdbreact";


class GamePage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            bid: {},
            isLoaded: false,
            error: null
        }
    }
    componentDidMount() {
        Axios.get("http://localhost:3000/bid")

            .then((result) => {
                this.setState({
                    isLoaded: true,
                    bid: result.data
                });

            },
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                });
    }

    render() {
        const {bid, isLoaded, error} = this.state;
        if (!isLoaded) {
            return <Spinner animation="grow" variant="warning" />
          } else {
            return (
            <Col md={{ span: 6, offset: 3 }}>
            <Card cla ssName="jawn" bg="dark" text="white">
                <Card.Text>Betting Ticket</Card.Text>
                <Card.Body>
      <Card.Title>{bid.teamOneName} vs {bid.teamTwoName}</Card.Title>

      <Card.Text>
        Time: {moment(new Date(parseInt(bid.commencementTime * 1000))).format('MMMM Do YYYY, h:mm a')}
      </Card.Text>
      <ButtonToolbar>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button className="bidbutton" variant="warning" value="#"></Button>
    <Button  variant="warning" value="#"></Button>
    <Button variant="warning" value="#"></Button>
    <Button variant="warning" value="#"></Button>
        </ButtonToolbar>
    </Card.Body>
            </Card>
            </Col>
        );
    };
};
};
export default GamePage;
