      super(props, context);

      this.handleSelect = this.handleSelect.bind(this);

      this.state = {
        index: 0,
        direction: null,
        thumbnails: []
      };
    }
  componentDidMount() { 
    Axios.get("http://localhost:3001/thumbnail")

    .then((result) => {
      this.setState({
        isLoaded: true,
