import React from "react";
import Carousel from 'react-bootstrap/Carousel';
import Axios from "axios";
import Spinner from "react-bootstrap/Spinner"

class ControlledCarousel extends React.Component {
    constructor(props, context) {
      super(props, context);

      this.handleSelect = this.handleSelect.bind(this);

      this.state = {
        index: 0,
        direction: null,
        thumbnails: [],
        isLoaded: false
      };
    }
  componentDidMount() { 
    Axios.get("http://localhost:3001/thumbnail")

    .then((result) => {
      console.log(result)
      this.setState({
        isLoaded: true,
        thumbnails: result.data

      });

    },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        })
      }
    )

};
    handleSelect(selectedIndex, e) {
      this.setState({
        index: selectedIndex,
        direction: e.direction,
      });
    }

    render() {
      const { index, direction, isLoaded, thumbnails} = this.state;
      console.log(thumbnails)
      if (!isLoaded) {
        return  <Spinner animation="grow" variant="warning" />
      } else { 
      return (
        <Carousel
          activeIndex={index}
          direction={direction}
          onSelect={this.handleSelect}
        >
          <div>
                    <Carousel.Item>
                    <img
                      className="d-block w-100"
                      src="//b.thumbs.redditmedia.com/1QvyeC4bGW6T_Y1wEdHwGQvmV3lxxtKSA63B5YOtFlo.jpg"
                    />
                  </Carousel.Item>

          </div>
        </Carousel>
      );
    }
  }
}

  export default ControlledCarousel
