import React from "react";
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import moment from 'moment';
import Axios from "axios";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Spinner from "react-bootstrap/Spinner";
import { MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from "mdbreact";
import { BrowserRouter } from 'react-router-dom';

class GamePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bid: {},
      bidamount: undefined,
      isLoaded: false,
      credits: null,
      currentBid: {},
      loggedIn: false
    }
    this.getUser = this.getUser.bind(this);
    this.componentDidMount = this.componentDidMount.bind(this);
    this.updateUser = this.updateUser.bind(this);
  }

  updateUser(userObject) {
    this.setState(userObject)
  };

  getUser() {
    Axios.get('http://localhost:3001/user').then(response => {
      console.log(response.data);
      if (response.data.user) {
        console.log('Get User: There is a user saved in the server session: ');

        this.setState({
          loggedIn: true,
          username: response.data.user.username
        })
      } else {
        console.log('Get user: no user');
        this.setState({
          loggedIn: false,
          username: null
        });
      };
    });
  };

  componentDidMount() {
    this.getUser();
    var userObject = JSON.parse(localStorage.getItem("userObject"));
    Axios.get("http://localhost:3001/bid")
      .then((result) => {
        this.setState({
          isLoaded: true,
          bid: result.data
        }, () => {
          for (var i = 0; i < result.data.length; i++) {
            if (("/bid/" + result.data[i]._id) === window.location.pathname) {
              this.setState({
                currentBid: result.data[i],
                credits: userObject.credits
              })

            }
          }
        });
      },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        });
  }

  onClickHandler = event => {
    var userObject = JSON.parse(localStorage.getItem("userObject"));
    const bidAmount = event.target.innerHTML;
    this.setState({ bidAmount });
    userObject.credits = userObject.credits - bidAmount;
    console.log(userObject)
    Storage.setItem("userObject")
    this.setState({
      credits: userObject.credits
    })
  //  Axios.get('http://localhost:3001/user/login').then(response => {
  //    console.log(response)
  //  })
    // Axios
    //   .put('http://localhost:3001/user/login', {
    //     credits: (this.state.credits - bidAmount)
    //   })
    //   .then(response => {
    //     console.log(response)
    //     console.log("Wager Placed!")
    //     this.setState({
    //       redirectTo: '/'
    //     })
    //   }).catch(error => {
    //       console.log(error)
    //   });

      var id = this.state.currentBid._id;
    Axios.put('http://localhost:3001/bid/', {
      _id: id,
      teamOneBidAmount: this.state.bidAmount,
      teamOneBettorName: userObject.username,
      betPlaced: true
    })
    .then(response => {
      console.log(response)
    })
  };
  render() {

    const { bid, isLoaded, loggedIn, credits, bidAmount, currentBid } = this.state;
    if (!isLoaded) {
      return <Spinner animation="grow" variant="warning" />
    } else {
      return (

        <Col md={{ span: 6, offset: 3 }}>
          <Card className="jawn" bg="dark" text="white">
            <Card.Text>Credits: {this.state.credits}</Card.Text>
            <Card.Text>Betting Ticket</Card.Text>
            <Card.Body>
              <Card.Title>{this.state.currentBid.teamOneName} vs {this.state.currentBid.teamTwoName}</Card.Title>

              <Card.Text>
                Time: {moment(new Date(parseInt(this.state.currentBid.commencementTime * 1000))).format('MMMM Do YYYY, h:mm a')}
              </Card.Text>

              <Form.Label>Bid Amount</Form.Label>
              <Row>
              <MDBDropdown className="MBDDropdown">
                      <MDBDropdownToggle id="upcoming-games" className="team1-button" caret color="warning">
                      {this.state.currentBid.teamOneName}
                        </MDBDropdownToggle>
                      <MDBDropdownMenu basic>
                        <MDBDropdownItem active={this.state.bidAmount === 5} onClick={this.onClickHandler}>$5</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 10} onClick={this.onClickHandler}>$10</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 25} onClick={this.onClickHandler}>$25</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 50} onClick={this.onClickHandler}>$50</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 100} onClick={this.onClickHandler}>$100</MDBDropdownItem>
                      </MDBDropdownMenu>
                    </MDBDropdown>
                    <MDBDropdown className="MBDDropdown">
                      <MDBDropdownToggle id="upcoming-games" className="team2-button" caret color="warning">
                      {this.state.currentBid.teamTwoName}
                        </MDBDropdownToggle>
                      <MDBDropdownMenu basic>
                        <MDBDropdownItem active={this.state.bidAmount === 5} onClick={this.onClickHandler}>$5</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 10} onClick={this.onClickHandler}>$10</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 25} onClick={this.onClickHandler}>$25</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 50} onClick={this.onClickHandler}>$50</MDBDropdownItem>
                        <MDBDropdownItem active={this.state.bidAmount === 100} onClick={this.onClickHandler}>$100</MDBDropdownItem>
                      </MDBDropdownMenu>
                    </MDBDropdown>
              </Row>
            </Card.Body>
          </Card>
        </Col>
      );
    };
  };
};
export default GamePage;
