import React, { Component } from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import moment from 'moment';
import ControlledCarousel from './carousel';
import Axios from "axios";


class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      articles: []
    }
  };


  componentDidMount() {
    var apiKey = process.env.apiKey;
    fetch("https://api.the-odds-api.com/v3/odds?sport=soccer_usa_mls&region=us&mkt=h2h&apiKey=71bb5796ac3d48b345d68c07d65c5bbb")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result.data
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }

      );
    Axios.get("http://localhost:3001/scrape")

      .then((result) => {
       console.log(result)
        this.setState({
          isLoaded: true,
          articles: result.data
        });

      },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          })
        }
      )

  };


  render() {
    const { error, isLoaded, items, articles } = this.state;
    // console.log(items)
    // console.log(articles)
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading Games...</div>;
    } else {
      return (
        <div>
          <Container>
            <ControlledCarousel />
            <Row>

              <Col sm>
                <Card bg="dark" text="white">
                  <Card.Body>
                    <Card.Title>Upcoming Games</Card.Title>
                  </Card.Body>
                </Card>
                <br></br>
                <div> {items.map(item => (
                  <Card bg="dark" text="white">
                    <Card.Body>
                      <Card.Title>{item.teams[0]} vs {item.teams[1]}</Card.Title>
                      <Card.Subtitle className="mb-2 text-muted">{item.home_team} at Home</Card.Subtitle>
                      <Card.Text>
                        League: {item.sport_nice}
                      </Card.Text>
                      <Card.Text>
                        Time: {moment(new Date(parseInt(item.commence_time * 1000))).format('MMMM Do YYYY, h:mm a')}
                      </Card.Text>
                      <Card.Subtitle>Bet On:  </Card.Subtitle>
                      <Button href="#" variant="outline-dark">{item.teams[0]}</Button>
                      <Button href="#" variant="outline-dark">{item.teams[1]}</Button>
                    </Card.Body>
                  </Card>
                ))}

                </div>
              </Col>
              <Col sm>
                <Card bg="dark" text="white">
                  <Card.Body>
                    <Card.Title>Sports News</Card.Title>
                  </Card.Body>
                </Card>

                <br></br>

                <div> { articles.map(article => ( 
                <Card bg="dark" text="white">
                  <Card.Body>
                    <Card.Title>{article.title}</Card.Title>
                    <Card.Subtitle className="mb-2 text-muted"></Card.Subtitle>
                  </Card.Body>
                </Card>
                ))}
                </div>

              </Col>
            </Row>
          </Container>
        </div>
      )
    };
  };
};



export default Home;
